for target in $(cat target.txt);do mkdir -p OUT-WEB-BIRD/$target && nikto -maxhosts -1 -nointeractive -nossl -maxtime 30m -useragent Mozilla/5.0 -host "$target" >> OUT-WEB-BIRD/$target/$target-nikto && nikto -maxhosts -1 -nointeractive -ssl -maxtime 30m -useragent Mozilla/5.0 -host "$target" >> OUT-WEB-BIRD/$target/$target-s-nikto;done
